CREATE FUNCTION public."GetActivityChildren"(activityid integer, activitiesids character varying) RETURNS TABLE("Id" integer, "Code" character varying, "DicParentId" integer, "IsDeleted" boolean, "Name" text, "NameLanguage1" text, "NameLanguage2" text, "ParentId" integer, "Section" character varying, "VersionId" integer, "ActivityCategoryLevel" integer)
    LANGUAGE plpgsql
    AS $$
BEGIN
    RETURN QUERY(
    WITH RECURSIVE "ActivityCte" AS
    (
        SELECT
          ac."Id"
        , ac."Code"
        , ac."DicParentId"
        , ac."IsDeleted"
        , ac."Name"
        , ac."NameLanguage1"
        , ac."NameLanguage2"
        , ac."ParentId"
        , ac."Section"
        , ac."VersionId"
        , ac."ActivityCategoryLevel"
        FROM "ActivityCategories" ac
        WHERE CONCAT(',', activitiesids, ',') LIKE CONCAT('%,',ac."Id", ',%') OR ac."Id" = activityid

    UNION ALL

        SELECT
          ac."Id"
        , ac."Code"
        , ac."DicParentId"
        , ac."IsDeleted"
        , ac."Name"
        , ac."NameLanguage1"
        , ac."NameLanguage2"
        , ac."ParentId"
        , ac."Section"
        , ac."VersionId"
        , ac."ActivityCategoryLevel"
    FROM "ActivityCategories" ac
        INNER JOIN "ActivityCte"
    ON "ActivityCte"."Id" = ac."ParentId")

    SELECT * FROM "ActivityCte");
END;
$$;


CREATE FUNCTION public."GetActivityParent"(activity_id integer, level integer, param_name character varying) RETURNS character varying
    LANGUAGE plpgsql
    AS $$
DECLARE
    res VARCHAR(2000);
BEGIN
    WITH RECURSIVE
    activity_tree AS
    (
        SELECT id, parentid, 0 AS lvl, Code, "Name"
        FROM "ActivityCategories"
        WHERE id = activity_id

        UNION ALL

        SELECT ac.id, ac.parentid, tr.lvl + 1, ac.Code, ac."Name"
        FROM activity_tree tr
        JOIN "ActivityCategories" ac ON tr.parentid = ac.id
    ),

    activity_levels AS
    (
        SELECT ROW_NUMBER() OVER (ORDER BY lvl DESC) AS level,
        tr.*
        FROM activity_tree tr
    )

    SELECT INTO res
        CASE LOWER(param_name)
            WHEN 'code' THEN "Code"
            WHEN 'name' THEN "Name"
        END
    FROM activity_levels
    WHERE level = level;

    RETURN res;
END;
$$;


