CREATE FUNCTION public."GetRegionParent"(regionid integer, level integer) RETURNS integer
    LANGUAGE plpgsql
    AS $_$
DECLARE
    res INTEGER;
BEGIN
    WITH RECURSIVE region_tree AS
    (
        SELECT id, parentid, 0 AS lvl
        FROM "Regions"
        WHERE id = regionid
        UNION ALL
        SELECT r.id, r.parentid, lvl + 1
        FROM region_tree AS p
        INNER JOIN regions r ON p.parentid = r.id
    ),
    region_tree_levels AS
    (
        SELECT row_number() OVER (ORDER BY lvl DESC) AS level,
        region_tree.*
        FROM region_tree
    )
    SELECT id INTO res
    FROM region_tree_levels
    WHERE level = $2;

    RETURN res;
END;
$_$;


