CREATE FUNCTION public.getreportstree(p_user character varying) RETURNS TABLE(id integer, title character varying, type character varying, reportid integer, parentnodeid integer, isdeleted boolean, resourcegroup character varying, reporturl character varying)
    LANGUAGE plpgsql
    AS $$
DECLARE
    query TEXT;
BEGIN
    query := 'SELECT
                    Id,
                    Title,
                    Type,
                    ReportId,
                    ParentNodeId,
                    IsDeleted,
                    ResourceGroup,
                    NULL as ReportUrl
               FROM "ReportTreeNode" rtn
               WHERE rtn.IsDeleted = FALSE
                 AND (rtn.ReportId IS NULL OR rtn.ReportId IN (SELECT DISTINCT ReportId FROM ReportAce WHERE Principal = ''' || p_user || '''))';

    RETURN QUERY EXECUTE query;
END;
$$;


